package PatternPgms;

public class Program4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;
		int count=1;
	
	
		for(int i=0;i<n;i++) {
			count=i+1;
			for(int j=0;i>=j;j++) {
				
				System.out.print(count*3 +" ");
				count++;
			}
			System.out.println();
			
		} 


}

}
